import './App.css';
import { useState } from 'react';
import axios from 'axios';

function App() {
  const [status, setStatus] = useState('');
  const [secondsLeft, setSecondsLeft] = useState(0);
  const [message, setMessage] = useState('');
  const [token, setToken] = useState(0);
  const [username, setUsername] = useState(0);

  const getToken = () => {
    axios.get('http://127.0.0.1:8000/generar_token?cliente=jorge.ayala')
    .then(res => {
      console.log('res: ', res)
      setStatus(res.data.status)
      setMessage(res.data.message)
      setSecondsLeft(res.data.secondsLeft)
      setToken(res.data.token)
      setUsername(res.data.username)

    })
    .catch(error => {
      setStatus(500)
      setMessage('El servicio no está disponible por el momento')
    })
  }
  return (
    <div className="App">
      <button className="openModalBtn" onClick={getToken}>Obtener token virtual</button>
      {status ? <p>{status}</p> : null}
      {username ? <p>{username}</p> : null}
      {token ? <p>{token}</p> : null}
      {message ? <p>{message}</p> : null}
      {secondsLeft ? <p>Expira luego de {secondsLeft} segundos</p> : null}
    </div>
  );
}

export default App;
