import './App.css';
import { useState } from 'react';
import axios from 'axios';
// import styled from 'styled-components';
import Alert from 'react-bootstrap/Alert'

function App() {
  const [show, setShow] = useState(false);
  const [status, setStatus] = useState('');
  const [secondsLeft, setSecondsLeft] = useState(0);
  const [message, setMessage] = useState('');
  const [token, setToken] = useState(0);
  const [username, setUsername] = useState(0);

  const getToken = () => {
    axios.get('http://127.0.0.1:8000/generar_token?cliente=jorge.ayala')
    .then(res => {
      setShow(true)
      setStatus(res.data.status)
      setMessage(res.data.message)
      setSecondsLeft(res.data.secondsLeft)
      setToken(res.data.token)
      setUsername(res.data.username)

    })
    .catch(error => {
      setShow(false)
      setMessage('El servicio no está disponible por el momento')
    })
  }

  return (
    <GeneralContainer>
      <GetTokenButton onClick={getToken}>Usar token virtual</GetTokenButton>
      {show ? <Alert variant="danger" onClose={() => setShow(false)} dismissible>
        <Alert.Heading>¡Error!</Alert.Heading>
        <p>
        {message}
        </p>
      </Alert> : <Alert variant="success">
        <Alert.Heading>Hey, nice to see you</Alert.Heading>
        <p>
          Aww yeah, you successfully read this important alert message. This example
          text is going to run a bit longer so that you can see how spacing within an
          alert works with this kind of content.
        </p>
        <hr />
        <p className="mb-0">
          Whenever you need to, be sure to use margin utilities to keep things nice
          and tidy.
        </p>
      </Alert>}
      {status ? <p>{status}</p> : null}
      {username ? <p>Usuario: {username}</p> : null}
      {token ? <p>TOKEN: {token}</p> : null}
      {message ? <p>{message}</p> : null}
      {secondsLeft ? <p>Expira luego de {secondsLeft} segundos</p> : null}
    </GeneralContainer>
  );

}

const GeneralContainer = styled.div`
	padding: 40px;
	display: flex;
	flex-wrap: wrap;
	justify-content: center;
	gap: 20px;
`;

const GetTokenButton = styled.button`
	display: block;
	padding: 10px 30px;
	border-radius: 100px;
	color: #fff;
	border: none;
	background: #1766DC;
	cursor: pointer;
	font-family: 'Roboto', sans-serif;
	font-weight: 500;
	transition: .3s ease all;
	&:hover {
		background: #0066FF;
	}
`;

export default App;
